<?php//Removing an element from an array$v=array(1 => 1, 2 => 4, 3 => 9, 4 => 16);echo "Array:\n";print_r($v);$keys = array_keys($v);echo "Keys:\n";print_r($keys);assert($keys == array(1,2,3,4));/* Screen output:Array(    [0] => 1    [1] => 4    [2] => 9    [3] => 16    [4] => 25)Remove index 2 (has the value of 9)Array(    [0] => 1    [1] => 4    [3] => 16    [4] => 25)*/?>